import React, { useState } from "react"
import { Card, Col, Row } from "react-bootstrap"
import '../UserProfile/style.css'
import axios from "axios"

const UserProfile: React.FC = () => {
    var image_url: any
    var file: string
    var [userImage, setuserImage] = useState<string>("");
    const [userName, setuserName] = useState<string>("");
    const [passWordAlert, setpassWordAlert] = useState<string>("");
    const userEmail = localStorage.getItem("mail");
    const userLoginId = localStorage.getItem("loginId");
    const userPassword = localStorage.getItem("password");
    function previewFile(event: any) {
        var file = event.target.files[0];
        const reader = new FileReader();

        reader.onloadend = (e) => {
            image_url = reader.result;
        }
        if (file) {
            reader.readAsDataURL(file);
        }
    }
    const addUserProfileBackend = async () => {
        console.log("charan")
        userImage = image_url;
        await axios.post('https://localhost:7243/api/UserDetails/userProfile',
            {
                "loginId": userLoginId,
                "userName": userName,
                "emailId": userEmail,
                "password": userPassword,
                "image": userImage

            }

        )
            .then(e => console.log(e))
            .catch(error => console.log(error))
        window.location.reload();
        //console.log(JSON.stringify(obj))
    }

    return (

        <Row>
            <Col className='user-profile-form '>
                <div className="form">
                    <form className='user-profile-fields'>
                        <label htmlFor="select_img" className="form-field position-relative"><b>Select Image</b></label>
                        <input type="file" className="form-control form-field-value" id="image-input" name="imageurl" onChange={(event) => previewFile(event)} accept="image/jpeg, image/png, image/jpg" />
                        <label htmlFor="Last_name" className="form-field position-relative" ><b>User Name</b></label>
                        <input type="text" name="Last_name" className="form-field-value" value={userName}
                            onChange={(event) => { setuserName(event.target.value); }} />
                        <label htmlFor="employeeEmail" className="form-field"><b>Email</b></label>
                        <p className={`form-fields form-fields-color  'content-editable-hidden' : 'content-tohide position-relative'} `}>{userEmail}</p>
                        <input
                            type="button"
                            className="submit-btn black-color"
                            id="submit-btn-colour"
                            onClick={() => {

                                addUserProfileBackend()
                            }}
                            value="Submit"
                        />
                    </form>
                </div>

            </Col>

        </Row >


    )
}
export default UserProfile;
